    val listData: ArrayList<Country>
        get() {
            val list = arrayListOf<Country>()
            for (position in countryNames.indices) {
                val country = Country()
                country.name = countryNames[position]
                country.history = countryHistories[position]
                country.date = date[position]
                country.image = countryFlags[position]
                country.emblem = countryEmblems[position]
                country.capital = countryCapital[position]
                list.add(country)
            }
            return list
        }
